<script>
  import { collection, onSnapshot } from 'firebase/firestore';
  import { getFirestore } from 'firebase/firestore';

  let projects = [];
  const db = getFirestore();
  const query = collection(db, 'projects');

  onSnapshot(query, (snapshot) => {
    projects = snapshot.docs.map(doc => doc.data());
  });
</script>
<main class="home">
  <section class="section3">
    <!-- Existing code... -->
    <div class="projects-card">
      {#if projects.length > 0}
        <div class="projects-header">Projects ({projects.length})</div>
        <div class="projects-body">
          {#each projects as project (project.id)}
            <button>{project.name}</button>
          {/each}
        </div>
      {:else}
        <div class="projects-header">No projects available yet.</div>
      {/if}
    </div>
  </section>
  <section class="section4">
    {#if projects.length > 0}
      {#each projects as project (project.id)}
        <div class="card__1 card-vertical">
          <!-- Your existing code for each project -->
          <div class="card-header">
            <div class="card-image">
              <img src={project.image} alt={project.name}>
            </div>
          </div>
          <div class="card-body">
            <div class="card-tags">
              {#each project.tags as tag}
                <span class="badge badge-primary badge-outline">{tag}  </span>
              {/each}
            </div>
            <div class="card-title">{project.name}</div>
            <div class="card-content">
              <p>{project.description}</p>
            </div>
	<div class="card-buttons">
            <button><a href="{project.demo}">Demo</a></button>
            <button><a href="{project.code}">Code</a></button>
</div>
          </div>
        </div>
      {/each}
    {/if}
  </section>
</main>
